    fun onClick(view : View) {

        setContentView(R.layout.city_info)
        val textView =  view.findViewById(R.id.city_text) as TextView
        val cityName = textView.getText().toString()
        println("clicked : name $cityName");
        println("Click")
        val cityNameView : TextView = findViewById(R.id.cityName)
        val cityDegreesView : TextView = findViewById(R.id.degrees)
        val cityIconView : TextView = findViewById(R.id.icon)
//        val cityForecastView : TextView = findViewById(R.id.foreacst)
        val cityImage0 : ImageView = findViewById(R.id.image0)
//        var cityName = cityNameView.setText(textView.getText().toString())
        val cityInfo = getCityInfoFromString(cityName)

        cityNameView.setText(cityInfo.name)
        cityDegreesView.setText(cityInfo.degrees)
        cityIconView.setText(cityInfo.condition)
//        cityForecastView.setText(cityInfo.forecast)
        Picasso.get()
            .load(cityInfo.pic0)
            .resize(800, 0)
            .placeholder(R.drawable.ic_launcher_foreground)
            .error(R.drawable.ic_launcher_foreground)
            .into(cityImage0)

        for (i in 1..3) {

            val minD : TextView = findViewById(resources.getIdentifier("dMin$i", "id", getPackageName()))
            minD.setText(cityInfo.forecastArray[i-1].min)
            val maxD : TextView = findViewById(resources.getIdentifier("dMax$i", "id", getPackageName()))
            maxD.setText(cityInfo.forecastArray[i-1].max)
            val condition : TextView = findViewById(resources.getIdentifier("condition$i", "id", getPackageName()))
            condition.setText(cityInfo.forecastArray[i-1].condition)
            val icon : ImageView = findViewById(resources.getIdentifier("image$i", "id", getPackageName()))
            Picasso.get()
                .load(cityInfo.forecastArray[i-1].pic)
//                .resize(0, 0)
                .placeholder(R.drawable.ic_launcher_foreground)
                .error(R.drawable.ic_launcher_foreground)
                .into(icon)
        }



    }

